select fit_installations_30_Jun_2013.installation_type,if(fit_installations_30_Jun_2013.llsoa_code = "","No LLSOA","LLSOA available") as no_llsoa,count(fit_installations_30_Jun_2013.fit_id), sum(fit_installations_30_Jun_2013.declared_net_capacity_kw) from fit_installations_30_Jun_2013 where technology_type="Photovoltaic" and fit_installations_30_Jun_2013.country_name not like "%Scotland%" group by fit_installations_30_Jun_2013.installation_type, no_llsoa desc