select fit_installations_30_Jun_2013.installation_type,if(fit_installations_30_Jun_2013.post_code_district = "","No postcode","Postcode available") as no_geo,count(fit_installations_30_Jun_2013.fit_id), sum(fit_installations_30_Jun_2013.declared_net_capacity_kw) from fit_installations_30_Jun_2013 where technology_type="Photovoltaic" group by fit_installations_30_Jun_2013.installation_type, no_geo